import React from 'react';

// React.Component Ŭ������ ��ӹ޴´�.
class App extends React.Component {

	state = {
		count: 0,
	};

	add = () => { this.state.count = 1; };
	sub = () => { console.log('subtract') };


	render() {
		return (
			<div>
				<h1> The number is: {this.state.count} </h1>
				<button onClick={this.add}> + </button>
				<button onClick={this.sub}> - </button>
			</div>
		);
	}
}

export default App;
