	componentDidMount() {
		setTimeout( () => {
			this.setState({isLoading: false});
		}, 6000);
	}


	render() {
		const { isLoading } = this.state;
		return (
			<div>{isLoading ? 'Loading...' : 'We are ready'}</div>
